    {id: 2, incometext: 'Bike Sold', incomeamount : 40000},
    {id: 3, incometext: 'Online', incomeamount: 20000 },
],
expensetranaction : [
    { id: 4, expensetext: 'Mobile purchase', incomeamount: 12000 },
    { id: 5, expensetext: 'Room Rent', incomeamount: 10000 },
    { id: 6, expensetext: 'Netflix Membership', incomeamount: 5000 },
],

};

export const GlobalContext = createContext(initialstate);

export const GlobalContextProvider = ({childern}) => {
    const [state, dispatch] = useReducer (AppReducer, initialstate)

    return(

        <GlobalContext.Provider value={{ 
            incometransaction: state.incometransaction, 
            expensetransaction: state.expensetransaction,}}>
              {childern}
        </GlobalContext.Provider>


    )

}
