import React, { useState, useContext } from 'react'
import { v4 as uuidv4 } from 'uuid'
import { GlobalContextProvider } from '../Context/Globalstate';


const Addtransaction = () => {

    const {addincome} = useContext

    const [income, setincome] = useState({
        incometext: '',
        incomeamount: 0,
    })

    const { incometext, incomeamount } = income;

    const onchangeincome = e => {
        setincome({ ...income, [e.target.name]: e.target.value });

    }

    const onsubmitincome = (e) => {
        e.preventdefault();


        const newincometransaction = {
            id: uuidv4(),
            incometext,
            incomeamount : incomeamount*1,
        }
        console.log(newincometransaction);

    }

    return (
        <div className='form-wrapper'>
            <form onSubmit={onsubmitincome}>
                <div className='input-group income'>

                    <input type='text'
                        placeholder='Add income...'
                        name='incometext'
                        autoComplete='off'
                        onChange={onchangeincome}></input>

                    <input type='number'
                        placeholder='Amount...'
                        name='incomeamount'
                        autoComplete='off'
                        onChange={onchangeincome}></input>

                    <input type='submit'
                        value='submit'
                    ></input>

                </div>
            </form>
            <form>
                <div className='input-group expense'>
                    <input type='text' placeholder='Add Expense...' autoComplete='off'></input>
                    <input type='number' placeholder='Amount...' autoComplete='off'></input>
                    <input type='submit' value='submit'></input>
                </div>
            </form>
        </div>
    )
}

export default Addtransaction;
