import AppReducer from './AppReducer';

const initialstate = {
    incometransaction: [
        { id: 1, incometext: 'Salary', incomeamount: 60000 },
        { id: 2, incometext: 'Bike Sold', incomeamount: 40000 },
        { id: 3, incometext: 'Online', incomeamount: 20000 },
